for (let j = 0, i = 0; i < targetIds.length; i++) {
  var target = document.getElementById(targetIds[i]);
  if (target) targets[j++] = target;
}
return targets;


var forAttrs =  this.getAttribute("opfor");
if (!forAttrs) return null;
var optargetIds = forAttrs.split(",");
